import React from 'react';
import { Field,reduxForm} from 'redux-form';
import { connect } from 'react-redux';
import { createpost } from '../../actions/index';


class CreatePost extends React.Component{

    renderError({touched,error}){
        if(touched && error){
            return(
                <div className="ui error message">
                    <div className="header">{error}</div>
                </div>
            );
        }
    }

    renderInput = ({input,label,meta,inputType,idLable }) => {
        const className = `field ${meta.error && meta.touched ? 'error' : ''}`;
        //console.log(meta);

        return(
                <div className={className}>
                    <label>{label}</label>

                    <input {...input}  autoComplete="off" />
                    {this.renderError(meta)}
            </div>
        );

    }
    /*if(inputType === "select") {
        /* onScroll={this.onScrollThatCallsPreventDefault}
      onScrollPassive={this.onScrollThatJustListens} */


    onSubmit = (formValues) => {
        this.props.createpost(formValues);

    }

    render(){
        //console.log(this.props);
        //if(localStorage.getItem("authToken")){
        return(
            <div>
                <h2>Create Post</h2 >
            <form onSubmit={this.props.handleSubmit(this.onSubmit)} className="ui form error">
                <Field inputType="text" name="Title" component={this.renderInput} label="Enter Title" />
                <Field inputType="text" name="Description" component={this.renderInput} label="Enter Description" />
               <Input
            type="select"
            name="status"
            component={this.renderInput}
            label="Status"
            multiple
          >
            <option>1</option>
            <option>2</option>
            <option>3</option>
            <option>4</option>
            <option>5</option>
          </Input>
                <button className="ui button primary">Submit</button>
            </form>
            </div>

        );
    }
}



const validate = (formValues) => {
    const error = {};

    if(!formValues.Title){
        error.Title = 'Please Enter a title';
    }

    if(!formValues.Description){
        error.Description = 'Please Enter a description';
    }

    if(!formValues.Status){
        error.Status = 'Please Enter a Status';
    }

    return error;
}


const fromWrapped = reduxForm({
    form : 'CreatePost',
    validate:validate
})(CreatePost);

export default connect(null,{createpost})(fromWrapped);
