import React from 'react';
import { Link } from 'react-router-dom';
import {signInAction} from '../actions/index';
import {RegisterUserAction} from '../actions/index';
import { connect } from 'react-redux';


class Header extends React.Component{

    renderSingInandSingOut(){
       // if(localStorage.getItem("authToken")){

            if(localStorage.setItem("authToken") === false){
                return(
                    <div className="right menu">
                            <button className="ui button primary">
                                <Link to="/user/signin" className="item">Log out</Link>
                            </button>

                        </div>

                );
            }
                else if(localStorage.setItem("authToken") === true){
                    return(
                    <div className="right menu">
                    {this.renderSingInandSingOut()}
                    <button className="ui button negative">
                        <Link to="/user/logout" className="item">Sign In</Link>
                    </button>
                </div>
                    );
                }
            }


                 render(){   
                     return(
                    <div className="ui secondary pointing menu">
                      <Link to="/" className="item">Posts</Link>
                      <Link to="/posts/createpost" className="item">Create Post</Link>
                      <Link to="/posts/userupdate" className="item">User Update</Link>


                        <div className="right menu">
                            <button className="ui button primary">
                            <Link to="/user/register" className="item">Registration</Link>
                            </button>
                        </div>

                    </div>
                     );
                 }   

            }






export default connect (null,{RegisterUserAction,signInAction})(Header);
