import wp from '../apis/wp';

export const signIn = formValue => async (dispatch) => {

    const response = await wp.post('jwt-auth/v1/token',{...formValue});
    console.log(response)
   // dispatch({ type :  , payload : response.data});
    //Do some programmatic navigation to
    //get the user back to the root route
    //history.push('/'); 

    };

    export const Register = formValue => async (dispatch) => {
        const response = await wp.post('wp/v2/users/register',{...formValue});
        console.log(response);
    };

    export const CreatePost = formValue => async (dispatch) => {
        const response = await wp.post('wp/v2/posts',{...formValue});
        console.log(response);
    };

    export const PostList = formValue => async (dispatch) => {
        const response = await wp.get('wp/v2/posts',{...formValue});
        console.log(response);
    };

    export const UpdatePost = formValue => async (dispatch) => {
        const response = await wp.put('wp/v2/posts/id',{...formValue});
        console.log(response);
    };

    export const DeletePost = formValue => async (dispatch) => {
        const response = await wp.delete('wp/v2/posts/id',{...formValue});
        console.log(response);
    }
