import React from 'react';
import { connect } from 'react-redux';
import { signInAction,signOutAction} from '../../';

class GoogleAuth extends React.Component{

    state = {token : null};

    /*componentDidMount(){
        window.gapi.load('client:auth2', () => {
            window.gapi.client.init({
                clientId:'930263169550-9177sdnu80f6k3ppgh1iomuncjsfn3fu.apps.googleusercontent.com',
                scope:'email' 
            }).then(() => {
                this.auth = window.gapi.auth2.getAuthInstance();
                this.onAuthChange(this.auth.isSignedIn.get());



                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    }*/

    onAuthChange = (isSignedIn) =>{
       if(isSignedIn){
           this.props.signIn(this.data.token);
       }
       else{
           this.props.signOut();
       }
    };

    onSignInClick = (id) => {
        this.props.signInAction(id);
    };

    onSignOutClick = () => {
        this.props.signOutAction();
    };  


    renderAuthButton(){
        if(this.props.data.token === null){
            return null;
        }
        else if(this.props.data.token){
            return (
                <button onClick={this.onSignOutClick} className="ui red button">
                    <i className=" icon" />
                    Sign Out
                </button>
            );
        }
        else {
            return(
                <button onClick={this.onSignInClick} className="ui red  button">
                    <i className=" icon" />
                    Sign In 
                    </button>
            );
        }
    }

    render(){
        return<div>{this.renderAuthButton()}</div>;
    }
}

const mapStateToProps = state => {
    return{ isSignedIn : state.data.token};
}

export default connect (mapStateToProps,{signInAction,signOutAction})(GoogleAuth);
