import React from 'react';
import { Field,reduxForm} from 'redux-form';

class Register extends React.Component{
    renderError({error,touched}){
        console.log({error});

        if(touched ){
            return(
                <div className="ui error message">
                    <div className="header">{error}</div>
                </div>
            );
        }
    }

    renderInput = ({input,label,meta}) => {
        const className = `field ${meta.error && meta.touched ? 'error' : ''}`;

        return(
                <div className={className}>
                    <label>{label}</label>
                    <input {...input} autoComplete="off" />
                    {this.renderError(meta)}
            </div>
        );

    }

    onSubmit(formValues){
        console.log(formValues);

    }

    render(){
        console.log(this.props);
        return(
            <div>
                <h2>Registeration Form</h2 >
            <form onSubmit={this.props.handleSubmit(this.onSubmit)} className="ui form error">
                <Field  
                    inputType="text" 
                    name="first_name" 
                    component={this.renderInput} 
                    label="First Name" 
                />
                <Field  
                    inputType="text" 
                    name="last_name" 
                    component={this.renderInput} 
                    label="Last Name" 
                />
                <Field  
                    inputType="text" 
                    name="user_name" 
                    component={this.renderInput} 
                    label="UserName" 
                />
                <Field  
                    inputType="email" 
                    name="email_id" 
                    component={this.renderInput} 
                    label="Email ID" 
                />
                <Field  
                    inputType="password" 
                    name="password" 
                    component={this.renderInput} 
                    label="Password" 
                />
                <Field  
                    inputType="password" 
                    name="cfm_password" 
                    component={this.renderInput} 
                    label="Confirm Password" 
                />
                <button className="ui button primary">Submit</button>
            </form>
            </div>

        );
    }
}

const validate = (formValues) => {
    const error = {};

    if(!formValues.first_name){
        error.first_name = 'Please Enter First Name';
    }

    if(!formValues.last_name){
        error.last_name = 'Please Enter Last Name ';
    }

    if(!formValues.user_name){
        error.Status = 'Please Enter Username';
    }

    if(!formValues.email_id){
        error.Status = 'please enter email id';
    }

    if(!formValues.password){
        error.Status = 'Please Enter password';
    }

    return error;

}

export default reduxForm({
    form : 'Register',
    validate
})(Register);
//export default Register;
