import React from 'react';

class Register extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            firstName: '',
            lastName: '',
            email: '',
            username: '',
            password: '',
            conformpassword: ''
        }
    }


handleChange = (event) => {
    this.setState({
        [event.target.id]: event.target.value
    })
}

handleSubmit = (event) => {

        event.preventDefault();
        console.log(this.state);
}

render(){
    return(
        <div className="ui container">
            <form onSubmit={this.handleSubmit} className="ui white">
                <h5 className="ui grey-text text-darken-3">Register Here</h5>
                <div className="ui input-field">
                    <label htmlFor="firstname">First Name</label>
                    <input type="text" id="firstname" onChange={this.handleChange} />
                </div>


                <div className="ui input-field">
                    <label htmlFor="lastname">Last Name</label>
                    <input type="text" id="lastname" onChange={this.handleChange} />
                </div>
                <div className="ui input-field">
                    <label htmlFor="username">User Name</label>
                    <input type="text" id="username" onChange={this.handleChange} />
                </div>
                <div className="ui input-field">
                    <label htmlFor="email">Email</label>
                    <input type="email" id="email" onChange={this.handleChange} />
                </div>
                <br></br>
                <div className="ui input-field">
                    <label htmlFor="password">Password</label>
                    <input type="password" id="password" onChange={this.handleChange} />
                </div>
                <div className="ui input-field">
                    <label htmlFor="conform password">Conform Password</label>
                    <input type="password" id="conform password" onChange={this.handleChange} />
                </div>
                <br></br>
                <div className="ui input-field">
                    <button className="ui button primary">Register</button>
                </div>
            </form>
        </div>
    );
}

}
export default Register;
