import posts from '../apis/posts';
import history from '../history' 


import {
    POST_LIST,
    CREATE_POST,
    UPDATE_POST,
    DELETE_POST,
    LOGOUT__REQUESTED
} from './types'
//import history from '../history';
//import {toaster} from 'react-redux-toastr';

