import React from 'react';
import Modal from '../Modal'

class DeletePost extends React.Component{

renderAction (){ 
    const {id} =  this.props.match.params; 
    return(
   <React.Fragment>
   <button 
   onClick = {() => this.props.De(id)} 
   className="ui button negative"
   >
   Delete
   </button>
   <Link to="/" className="ui button">Cancel</Link>
   </React.Fragment>
    );
}

renderContent(){
   if(!this.props.stream){
       return `Are you sure to delete this stream?`;
   }
   return `Are you sure to delete this stream with Title : ${this.props.stream.title}`
}

render(){

return(
       <Model  
         title="Delete Stream"
         content={this.renderContent()}
         actions={this.renderAction()}
         onDismiss = {() => history.push('/')}
         />
);
}
}

const mapStateToProps = (state, ownProps) => {
return{ stream : state.streams[ownProps.match.params.id]}
}


export default connect(mapStateToProps,{ fetchStream , deleteStream })(DeletePost);
