import React from 'react';
import { Field,reduxForm} from 'redux-form';
import { RegisterUser } from '../../actions/index';
import { connect } from 'react-redux';

class Register extends React.Component{
    renderError({touched,error}){
        console.log({error});

        if(touched && error ){
            return(
                <div className="ui error message">
                    <div className="header">{error}</div>
                </div>
            );
        }
    }

    renderInput = ({input,type,label,meta}) => {
        const className = `field ${meta.error && meta.touched ? 'error' : ''}`;

        return(
                <div className={className}>
                    <label>{label}</label>
                    <input {...input} type={type} autoComplete="off" />
                    {this.renderError(meta)}
            </div>
        );

    }

    onSubmit(formValues){
        console.log(formValues);

    }

    render(){
        console.log(this.props);
        return(
            <div>
                <h2>Registeration Form</h2 >
            <form onSubmit={this.props.handleSubmit(this.onSubmit)} className="ui form error">
                <Field  
                    type="text" 
                    name="first_name" 
                    component={this.renderInput} 
                    label="First Name" 
                />
                <Field  
                    type="text" 
                    name="last_name" 
                    component={this.renderInput} 
                    label="Last Name" 
                />
                <Field  
                    type="text" 
                    name="username" 
                    component={this.renderInput} 
                    label="UserName" 
                />
                <Field  
                    type="email" 
                    name="email" 
                    component={this.renderInput} 
                    label="Email ID" 
                />
                <Field  
                    type="password" 
                    name="password" 
                    component={this.renderInput} 
                    label="Password" 
                />

                <Field  
                    type="password" 
                    name="cfm_password" 
                    component={this.renderInput} 
                    label="Confirm Password" 
                />

                <Field  
                    type="text" 
                    name="role" 
                    component={this.renderInput} 
                    label="Role" 
                />
                <button type="submit" className="ui button primary">Submit</button>
            </form>
            </div>

        );
    }
}

const validate = (formValues) => {
    const errors = {};

    if(!formValues.first_name){
        errors.first_name = 'Please Enter First Name';
    }
    else if (formValues.first_name.length > 15) {
    errors.first_name = 'Must be 15 characters or less'
  }

    if(!formValues.last_name){
        errors.last_name = 'Please Enter Last Name ';
    }
    else if (formValues.last_name.length > 15) {
        errors.last_name = 'Must be 15 characters or less'
      }

    if(!formValues.username){
        errors.username = 'Please Enter Username';
    }
    else if (formValues.username.length > 15) {
        errors.username = 'Must be 15 characters or less'
      }

    if(!formValues.email){
        errors.email = 'please enter email id';
    }
    else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(formValues.email)) {
        errors.email = 'Invalid email address'
      }

    if(!formValues.password){
        error.password = 'Please Enter password';
    }

    if(!formValues.cfm_password){
        error.cfm_password = 'Please confirm password';
    }

    if(!formValues.role){
        error.role = 'Please Enter Role';
    }


    return error;

}

const Wrapped = reduxForm({
    form : 'Register',
    validate
})(Register);

export default connect(null,{RegisterUser})(Wrapped);
